mapper.insert:
insert into lecture(lectureName
, teacher
, subjectName
, description
, price
, startDate
, target
, thumbnail
, lectureData
)
values(#{lectureName}
, #{teacher}
, #{subjectName}
, #{description}
, #{price}
, #{startDate}
, #{target}
, #{thumbnail}
, #{lectureData}
)

choose.when:
update lecture
set lectureName=#{dto.lectureName}
, teacher=#{dto.teacher}
, subjectName=#{dto.subjectName}
, description=#{dto.description}
, price=#{dto.price}
, startDate=#{dto.startDate}
, target=#{dto.target}
where lectureNo=#{dto.lectureNo}

update lecture
set lectureName=#{dto.lectureName}
, teacher=#{dto.teacher}
, subjectName=#{dto.subjectName}
, description=#{dto.description}
, price=#{dto.price}
, startDate=#{dto.startDate}
, target=#{dto.target}
, lectureData=#{dto.lectureData}
where lectureNo=#{dto.lectureNo}

update lecture
set lectureName=#{dto.lectureName}
, teacher=#{dto.teacher}
, subjectName=#{dto.subjectName}
, description=#{dto.description}
, price=#{dto.price}
, startDate=#{dto.startDate}
, target=#{dto.target}
, thumbnail=#{dto.thumbnail}
where lectureNo=#{dto.lectureNo}

choose.otherwise:
update lecture
