, subjectName=#{dto.subjectName}
, description=#{dto.description}
, price=#{dto.price}
, startDate=#{dto.startDate}
, target=#{dto.target}
, thumbnail=#{dto.thumbnail}
, lectureData=#{dto.lectureData}
